### SQL
SELECT r.area_name, SUM(e.main_working_female) AS val FROM education_stats e JOIN regions r ON e.state = r.state JOIN tru t ON e.tru_id = t.id WHERE t.name = 'Rural' GROUP BY r.area_name ORDER BY val DESC LIMIT 1;

