### SQL
SELECT r.area_name, ls.person FROM language_stats ls JOIN regions r ON ls.state = r.state JOIN languages l ON ls.language_id = l.id JOIN tru t ON ls.tru_id = t.id WHERE l.name = 'Hindi' AND r.area_name IN ('Madhya Pradesh', 'Rajasthan') AND t.name = 'Total' ORDER BY ls.person DESC;

