### SQL
SELECT (SUM(rs.f_lit) * 100.0 / NULLIF(SUM(rs.tot_f), 0)) as female_literacy_rate FROM religion_stats rs JOIN regions r ON rs.state = r.state JOIN religions rel ON rs.religion_id = rel.id JOIN tru t ON rs.tru_id = t.id WHERE r.area_name = 'Uttar Pradesh' AND rel.religion_name = 'Muslim' AND t.name = 'Total';

