### SQL
SELECT ls.male FROM language_stats ls JOIN regions r ON ls.state = r.state JOIN languages l ON ls.language_id = l.id JOIN tru t ON ls.tru_id = t.id WHERE r.area_name = 'Maharashtra' AND l.name = 'English' AND t.name = 'Total';

