### SQL
SELECT rs.tot_m FROM religion_stats rs JOIN regions r ON rs.state = r.state JOIN religions rel ON rs.religion_id = rel.id JOIN tru t ON rs.tru_id = t.id WHERE r.area_name = 'Tamil Nadu' AND rel.religion_name = 'Christian' AND t.name = 'Total';

