### SQL
SELECT rs.tot_work_p FROM religion_stats rs JOIN regions r ON rs.state = r.state JOIN religions rel ON rs.religion_id = rel.id JOIN tru t ON rs.tru_id = t.id WHERE r.area_name = 'Madhya Pradesh' AND rel.religion_name = 'Jain' AND t.name = 'Total';

