### SQL
SELECT r.area_name, ls.female FROM language_stats ls JOIN regions r ON ls.state = r.state JOIN languages l ON ls.language_id = l.id JOIN tru t ON ls.tru_id = t.id WHERE l.name = 'Hindi' AND r.area_name IN ('Bihar', 'Uttar Pradesh') AND t.name = 'Total' ORDER BY ls.female DESC;

