### SQL
SELECT ls.person FROM language_stats ls JOIN regions r ON ls.state = r.state JOIN languages l ON ls.language_id = l.id JOIN tru t ON ls.tru_id = t.id WHERE r.area_name = 'NCT of Delhi' AND l.name = 'Tamil' AND t.name = 'Urban';

