### SQL
SELECT rel.religion_name, (SUM(rs.non_work_p) * 100.0 / NULLIF(SUM(rs.tot_p), 0)) as non_work_rate FROM religion_stats rs JOIN regions r ON rs.state = r.state JOIN religions rel ON rs.religion_id = rel.id JOIN tru t ON rs.tru_id = t.id WHERE r.area_name = 'Kerala' AND t.name = 'Total' GROUP BY rel.religion_name ORDER BY non_work_rate DESC LIMIT 1;

