### SQL
SELECT r.area_name, rs.tot_p FROM religion_stats rs JOIN regions r ON rs.state = r.state JOIN religions rel ON rs.religion_id = rel.id JOIN tru t ON rs.tru_id = t.id WHERE rel.religion_name = 'Sikh' AND r.area_name IN ('Rajasthan', 'Himachal Pradesh') AND t.name = 'Total' ORDER BY rs.tot_p DESC;

