### SQL
SELECT (SUM(rs.p_lit) * 100.0 / NULLIF(SUM(rs.tot_p), 0)) as literacy_rate FROM religion_stats rs JOIN regions r ON rs.state = r.state JOIN tru t ON rs.tru_id = t.id WHERE r.area_name = 'Mizoram' AND t.name = 'Total';

