### SQL
SELECT ((SUM(rs.m_lit) * 100.0 / NULLIF(SUM(rs.tot_m), 0)) - (SUM(rs.f_lit) * 100.0 / NULLIF(SUM(rs.tot_f), 0))) as lit_gap FROM religion_stats rs JOIN regions r ON rs.state = r.state JOIN religions rel ON rs.religion_id = rel.id JOIN tru t ON rs.tru_id = t.id WHERE r.area_name = 'Rajasthan' AND rel.religion_name = 'Hindu' AND t.name = 'Total';

