### SQL
SELECT r.area_name, (SUM(rs.p_lit) * 100.0 / NULLIF(SUM(rs.tot_p), 0)) as literacy_rate FROM religion_stats rs JOIN regions r ON rs.state = r.state JOIN religions rel ON rs.religion_id = rel.id JOIN tru t ON rs.tru_id = t.id WHERE rel.religion_name = 'Jain' AND t.name = 'Total' GROUP BY r.area_name ORDER BY literacy_rate DESC LIMIT 1;

