### SQL
SELECT (SUM(rs.tot_f) * 1000.0 / NULLIF(SUM(rs.tot_m), 0)) as sex_ratio FROM religion_stats rs JOIN regions r ON rs.state = r.state JOIN religions rel ON rs.religion_id = rel.id JOIN tru t ON rs.tru_id = t.id WHERE r.area_name = 'Maharashtra' AND rel.religion_name = 'Buddhist' AND t.name = 'Total';

