### SQL
SELECT (SUM(rs.tot_work_p) * 100.0 / NULLIF(SUM(rs.tot_p), 0)) as work_participation_rate FROM religion_stats rs JOIN regions r ON rs.state = r.state JOIN religions rel ON rs.religion_id = rel.id JOIN tru t ON rs.tru_id = t.id WHERE r.area_name = 'Punjab' AND rel.religion_name = 'Sikh' AND t.name = 'Total';

