### SQL
SELECT rel.religion_name, rs.tot_work_p FROM religion_stats rs JOIN regions r ON rs.state = r.state JOIN religions rel ON rs.religion_id = rel.id JOIN tru t ON rs.tru_id = t.id WHERE r.area_name = 'Maharashtra' AND t.name = 'Total' ORDER BY rs.tot_work_p DESC LIMIT 1;

