### SQL
SELECT r.area_name FROM healthcare_stats h JOIN regions r ON h.state = r.state JOIN tru t ON h.tru_id = t.id WHERE t.name = 'Total' ORDER BY CAST(h.children_under_5_years_who_are_wasted_weightforheight18_ AS DOUBLE PRECISION) ASC LIMIT 1;

