### SQL
WITH urban_obesity AS (SELECT AVG((CAST(women_age_1549_years_who_are_overweight_or_obese_bmi_250_kgm AS DOUBLE PRECISION) + CAST(men_age_1549_years_who_are_overweight_or_obese_bmi_250_kgm2_ AS DOUBLE PRECISION))/2.0) as val FROM healthcare_stats h JOIN tru t ON h.tru_id = t.id WHERE t.name = 'Urban'), rural_obesity AS (SELECT AVG((CAST(women_age_1549_years_who_are_overweight_or_obese_bmi_250_kgm AS DOUBLE PRECISION) + CAST(men_age_1549_years_who_are_overweight_or_obese_bmi_250_kgm2_ AS DOUBLE PRECISION))/2.0) as val FROM healthcare_stats h JOIN tru t ON h.tru_id = t.id WHERE t.name = 'Rural') SELECT CASE WHEN u.val > r.val THEN 'Urban' ELSE 'Rural' END FROM urban_obesity u, rural_obesity r;

