### SQL
SELECT r.area_name FROM healthcare_stats h JOIN regions r ON h.state = r.state JOIN tru t ON h.tru_id = t.id WHERE t.name = 'Total' ORDER BY h.pop_living_in_hh_that_use_an_improved_sanitation_facility2_ DESC LIMIT 1;

