### SQL
SELECT r.area_name FROM healthcare_stats h JOIN regions r ON h.state = r.state JOIN tru t ON h.tru_id = t.id WHERE t.name = 'Total' ORDER BY h.pop_living_in_hh_with_an_improved_drinkingwater_source1_ ASC LIMIT 1;

