### SQL
SELECT r.area_name FROM healthcare_stats h JOIN regions r ON h.state = r.state JOIN tru t ON h.tru_id = t.id WHERE t.name = 'Total' AND CAST(h.women_age_2024_years_married_before_age_18_years_ AS DOUBLE PRECISION) > 20;

