### SQL
SELECT r.area_name FROM healthcare_stats h JOIN regions r ON h.state = r.state JOIN tru t ON h.tru_id = t.id WHERE t.name = 'Total' ORDER BY CAST(h.men_age_2529_years_married_before_age_21_years_ AS DOUBLE PRECISION) DESC LIMIT 1;

