### SQL
SELECT r.area_name FROM healthcare_stats h JOIN regions r ON h.state = r.state JOIN tru t ON h.tru_id = t.id WHERE t.name = 'Total' ORDER BY CAST(h.total_fertility_rate_number_of_children_per_woman AS DOUBLE PRECISION) DESC LIMIT 1;

