### SQL
SELECT r.area_name FROM healthcare_stats h JOIN regions r ON h.state = r.state JOIN tru t ON h.tru_id = t.id WHERE t.name = 'Total' AND CAST(h.total_fertility_rate_number_of_children_per_woman AS DOUBLE PRECISION) < 2.0;

