### SQL
SELECT r.area_name FROM healthcare_stats h JOIN regions r ON h.state = r.state JOIN tru t ON h.tru_id = t.id WHERE t.name = 'Total' ORDER BY h.adolescent_fertility_rate_for_women_age_1519_years5 DESC LIMIT 1;

