### SQL
SELECT r.area_name FROM healthcare_stats h JOIN regions r ON h.state = r.state JOIN tru t ON h.tru_id = t.id WHERE t.name = 'Total' ORDER BY CAST(h.women_age_1549_years_owning_a_house_andor_land_alone_or_join AS DOUBLE PRECISION) DESC LIMIT 1;

