### SQL
SELECT r.area_name FROM healthcare_stats h JOIN regions r ON h.state = r.state JOIN tru t ON h.tru_id = t.id WHERE t.name = 'Total' ORDER BY CAST(h.evermarried_women_age_1849_years_who_have_experienced_physic AS DOUBLE PRECISION) DESC LIMIT 1;

