### SQL
SELECT r.area_name FROM healthcare_stats h JOIN regions r ON h.state = r.state JOIN tru t ON h.tru_id = t.id WHERE t.name = 'Total' ORDER BY CAST(h.total_children_age_623_months_receiving_an_adequate_diet16_1 AS DOUBLE PRECISION) DESC LIMIT 1;

