### SQL
SELECT r.area_name FROM healthcare_stats h JOIN regions r ON h.state = r.state JOIN tru t ON h.tru_id = t.id WHERE t.name = 'Total' ORDER BY CAST(h.registered_pregnancies_for_which_the_mother_received_a_mothe AS DOUBLE PRECISION) DESC LIMIT 1;

