### SQL
SELECT r.area_name FROM healthcare_stats h JOIN regions r ON h.state = r.state JOIN tru t ON h.tru_id = t.id WHERE t.name = 'Total' ORDER BY CAST(h.births_in_a_public_health_facility_that_were_delivered_by_ca AS DOUBLE PRECISION) DESC LIMIT 1;

