### SQL
SELECT r.area_name FROM healthcare_stats h JOIN regions r ON h.state = r.state JOIN tru t ON h.tru_id = t.id WHERE t.name = 'Total' ORDER BY CAST(h.hh_with_any_usual_member_covered_under_a_health_insurancefin AS DOUBLE PRECISION) DESC LIMIT 1;

