SELECT r.area_name FROM healthcare_stats h JOIN regions r ON h.state = r.state JOIN tru t ON h.tru_id = t.id WHERE t.name = 'Total' ORDER BY (CAST(h.women_age_1549_years_who_are_overweight_or_obese_bmi_250_kgm AS DOUBLE PRECISION) + CAST(h.men_age_1549_years_who_are_overweight_or_obese_bmi_250_kgm2_ AS DOUBLE PRECISION))/2.0 DESC LIMIT 1;

