### SQL
SELECT r.area_name FROM healthcare_stats h JOIN regions r ON h.state = r.state JOIN tru t ON h.tru_id = t.id WHERE t.name = 'Total' AND (h.women_age_15_years_and_above_wih_high_or_very_high_140_mgdl_ + h.men_age_15_years_and_above_wih_high_or_very_high_140_mgdl_bl)/2.0 > 10;

