### SQL
WITH avg_hyp AS (SELECT AVG((women_age_15_years_and_above_wih_elevated_blood_pressure_sys + men_age_15_years_and_above_wih_elevated_blood_pressure_systo)/2.0) as val FROM healthcare_stats h JOIN tru t ON h.tru_id = t.id WHERE t.name = 'Total') SELECT r.area_name FROM healthcare_stats h JOIN regions r ON h.state = r.state JOIN tru t ON h.tru_id = t.id, avg_hyp WHERE t.name = 'Total' AND (h.women_age_15_years_and_above_wih_elevated_blood_pressure_sys + h.men_age_15_years_and_above_wih_elevated_blood_pressure_systo)/2.0 > avg_hyp.val;

