SELECT r.area_name FROM healthcare_stats h JOIN regions r ON h.state = r.state JOIN tru t ON h.tru_id = t.id WHERE t.name = 'Total' AND CAST(h.men_age_1549_years_who_are_anaemic_130_gdl22_ AS DOUBLE PRECISION) > h.all_women_15_49_who_are_anaemic22_;

