### SQL
SELECT r.area_name FROM healthcare_stats h JOIN regions r ON h.state = r.state JOIN tru t ON h.tru_id = t.id WHERE t.name = 'Total' AND CAST(h.children_age_1223_months_fully_vaccinated_based_on_informati AS DOUBLE PRECISION) > 90;

