### SQL
SELECT r.area_name FROM regions r JOIN healthcare_stats h1 ON r.state = h1.state JOIN tru t1 ON h1.tru_id = t1.id JOIN healthcare_stats h2 ON r.state = h2.state JOIN tru t2 ON h2.tru_id = t2.id WHERE t1.name = 'Rural' AND t2.name = 'Urban' AND ((h1.women_age_15_years_and_above_wih_elevated_blood_pressure_sys + h1.men_age_15_years_and_above_wih_elevated_blood_pressure_systo)/2.0) > ((h2.women_age_15_years_and_above_wih_elevated_blood_pressure_sys + h2.men_age_15_years_and_above_wih_elevated_blood_pressure_systo)/2.0);

