### SQL
SELECT (CAST(h.children_age_1223_months_fully_vaccinated_based_on_informati AS DOUBLE PRECISION) * h.number_of_hh_surveyed / 100) FROM healthcare_stats h JOIN regions r ON h.state = r.state JOIN tru t ON h.tru_id = t.id WHERE r.area_name = 'Bihar' AND t.name = 'Rural';

