### SQL
WITH tn_stats AS (SELECT t.name, (h.women_age_15_years_and_above_wih_elevated_blood_pressure_sys + h.men_age_15_years_and_above_wih_elevated_blood_pressure_systo)/2.0 as val FROM healthcare_stats h JOIN regions r ON h.state = r.state JOIN tru t ON h.tru_id = t.id WHERE r.area_name = 'Tamil Nadu' AND t.name IN ('Urban', 'Rural')) SELECT CASE WHEN (SELECT val FROM tn_stats WHERE name='Urban') > (SELECT val FROM tn_stats WHERE name='Rural') THEN 'Urban' ELSE 'Rural' END;

