### SQL
SELECT r.area_name FROM healthcare_stats h JOIN regions r ON h.state = r.state JOIN tru t ON h.tru_id = t.id WHERE t.name = 'Total' ORDER BY CAST(h.men_age_1549_with_10_or_more_years_of_schooling_ AS DOUBLE PRECISION) DESC LIMIT 1;

