### SQL
SELECT r.area_name FROM healthcare_stats h JOIN regions r ON h.state = r.state JOIN tru t ON h.tru_id = t.id WHERE t.name = 'Total' ORDER BY CAST(h.children_age_5_years_who_attended_preprimary_school_during_t AS DOUBLE PRECISION) ASC LIMIT 1;

