SELECT r.area_name FROM healthcare_stats h JOIN regions r ON h.state = r.state JOIN tru t ON h.tru_id = t.id WHERE t.name = 'Total' ORDER BY CAST(h.men_age_1549_who_have_ever_used_the_internet_ AS DOUBLE PRECISION) DESC LIMIT 1;

