### SQL
SELECT r.area_name FROM healthcare_stats h JOIN regions r ON h.state = r.state JOIN tru t ON h.tru_id = t.id WHERE t.name = 'Total' ORDER BY CAST(h.child_u5_whose_birth_was_registered_with_the_civil_authority AS DOUBLE PRECISION) ASC LIMIT 1;

