### SQL
SELECT r.area_name FROM healthcare_stats h JOIN regions r ON h.state = r.state JOIN tru t ON h.tru_id = t.id WHERE t.name = 'Total' ORDER BY CAST(h.deaths_in_the_last_3_years_registered_with_the_civil_authori AS DOUBLE PRECISION) DESC LIMIT 1;

