### SQL
SELECT r.area_name FROM healthcare_stats h JOIN regions r ON h.state = r.state JOIN tru t ON h.tru_id = t.id WHERE t.name = 'Total' ORDER BY CAST(h.hh_using_iodized_salt_ AS DOUBLE PRECISION) ASC LIMIT 1;

