### SQL
SELECT r.area_name FROM healthcare_stats h JOIN regions r ON h.state = r.state JOIN tru t ON h.tru_id = t.id WHERE t.name = 'Total' ORDER BY CAST(h.men_age_1549_years_who_have_high_risk_waisttohip_ratio_090_ AS DOUBLE PRECISION) ASC LIMIT 1;

