SELECT r.area_name FROM healthcare_stats h JOIN regions r ON h.state = r.state JOIN tru t ON h.tru_id = t.id WHERE t.name = 'Total' ORDER BY CAST(h.pregnant_women_15_49_who_are_anaemic_110_gdl22_ AS DOUBLE PRECISION) DESC LIMIT 1;

