### SQL
SELECT r.area_name FROM healthcare_stats h JOIN regions r ON h.state = r.state JOIN tru t ON h.tru_id = t.id WHERE t.name = 'Total' ORDER BY CAST(h.women_age_15_years_and_above_with_high_141160_mgdl_blood_sug AS DOUBLE PRECISION) DESC LIMIT 1;

