### SQL
SELECT r.area_name FROM healthcare_stats h JOIN regions r ON h.state = r.state JOIN tru t ON h.tru_id = t.id WHERE t.name = 'Total' ORDER BY h.women_age_15_years_and_above_wih_elevated_blood_pressure_sys DESC LIMIT 1;

