SELECT r.area_name FROM healthcare_stats h JOIN regions r ON h.state = r.state JOIN tru t ON h.tru_id = t.id WHERE t.name = 'Total' ORDER BY CAST(h.men_age_3049_yearsever_undergone_an_oral_cavity_examination_ AS DOUBLE PRECISION) DESC LIMIT 1;

